
/*! Simulovaná html5-qrcode knihovna */
class Html5QrcodeScanner {
  constructor(id, config, verbose) {
    this.id = id; this.config = config; this.verbose = verbose;
  }
  render(successCallback, errorCallback) {
    const div = document.getElementById(this.id);
    div.innerHTML = '<p>[Simulace QR kamery – ve verzi produkce bude kamera aktivní]</p>';
    setTimeout(() => successCallback("Test Location;50.087;14.421;map_test.png"), 2000);
  }
}
